			pr.setString( 1, STR_VALUE1);
			pr.setInt( 2, 1 );
			pr.execute();
			pr.setString( 1, STR_VALUE1);
			pr.setInt( 2, 2 );
			pr.execute();
			pr.setString( 1, STR_VALUE1);
			pr.setNull( 2, Types.INTEGER );
			pr.execute();
			pr.setString( 1, STR_VALUE2);
