    private int countFiles(String fileNameStart){
        int count = 0;
        String names[] = new File(AllTests.CATALOG).list();
        for(int i=0; i<names.length; i++){
            if(names[i].startsWith(fileNameStart)){
                count++;
            }
        }
        return count;
    }


    public void testAmbiguousColumn() throws Exception{
