	private void checkValues(Statement st, Object[] values, boolean needTrim) throws Exception{
        ResultSet rs = st.executeQuery("SELECT * From " + table);

        int i = 0;
        while(rs.next()){
            assertEqualsRsValue(values[i], rs, needTrim);
            i++;
        }
        rs.close();
	}


